import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import credentialsVerify from '../Helpers/credentialsVerify';
import submitLocalStorage from '../Helpers/submitLocalStorage';
import '../styles/Login.css';

function Login() {
  const history = useHistory();
  function HandlerLogin() {
    history.push('/comidas');
  }
  const [user, setUser] = useState({ email: '', password: '' });
  const [disabled, setDisabled] = useState(true);

  function handleSubmit(e) {
    setUser({
      ...user, [e.target.name]: e.target.value,
    });
  }

  useEffect(() => {
    setDisabled(credentialsVerify(user.email, user.password));
  }, [user]);

  return (
    <div className="d-flex justify-content-center">
    <div className="container-fluid">
      <h1 className="d-flex justify-content-center">Login</h1>
      <section className="d-flex flex-wrap justify-content-center">
        <label htmlFor="email">
          <input
            onChange={ (e) => handleSubmit(e) }
            name="email"
            data-testid="email-input"
            id="email"
            type="email"
            placeholder="Email"
          />
        </label>
        <label htmlFor="password">
          <input
            onChange={ (e) => handleSubmit(e) }
            name="password"
            data-testid="password-input"
            id="password"
            type="password"
            placeholder="Senha"
          />
        </label>
      </section>
      <div className="d-flex justify-content-center">
        <button
          // className="d-flex login-button"
          disabled={ disabled }
          type="button"
          data-testid="login-submit-btn"
          onClick={ () => { submitLocalStorage(user.email); HandlerLogin(); } }
        >
          Entrar
        </button>
      </div>
    </div>
    </div>
  );
}

export default Login;
