import React, { useEffect, useState } from 'react';
import profileIcon from '../../images/profileIcon.svg';
import searchIcon from '../../images/searchIcon.svg';
import FooterBar from '../Components/FooterBar';

function ExploreFoodsArea() {
  const [areas, setAreas] = useState([]);
  const [firstFoods, setFirstFoods] = useState([]);
  useEffect(() => {
    // Requisita locais de origem
    async function requestAreas() {
      const URL = 'https://www.themealdb.com/api/json/v1/1/list.php?a=list';
      const request = await fetch(URL);
      const response = await request.json();
      setAreas(response.meals);
    }
    requestAreas();
  }, []);
  return (
    <>
      <div>
        {areas && console.log(areas)}
        <h1 data-testid="page-title">Explorar Origem</h1>
        <img
          data-testid="search-top-btn"
          src={ searchIcon }
          alt="Botão que direciona para a tela de perfil"
        />
        <img
          data-testid="profile-top-btn"
          src={ profileIcon }
          alt="Botão que direciona para a tela de perfil"
        />
      </div>
      <select data-testid="explore-by-area-dropdown">
        {areas.length > 0 ? areas.map(({ strArea }, i) => (
          <option key={ `${strArea}${i}` } data-testid={ `${strArea}-option` }>{strArea}</option>
        )) : <option>Carregando...</option>}
      </select>
      <FooterBar />
    </>
  );
}

export default ExploreFoodsArea;
